mapper.insert:
insert into contribute (title, brief_introduction, url, summary, user_id, picture, create_time, update_time)
values (#{title}, #{briefIntroduction}, #{url}, #{summary}, #{userId}, #{picture}, #{createTime},
        #{updateTime});

mapper.update:
UPDATE contribute
title = #{title}, brief_introduction = #{briefIntroduction}, url = #{url}, summary = #{summary}, user_id = #{userId}, picture = #{picture}, create_time = #{createTime}, update_time = #{updateTime},
WHERE id = #{id}
